        <script>
            document.getElementById('topCustomersTimeRange').addEventListener('change', function() {
                $.ajax({
                    url: '{{ route('top.customers') }}',
                    type: 'GET',
                    data: {
                        timeRange: this.value
                    },
                    beforeSend: function() {
                        $('#topCustomersTable tbody').html(
                            '<tr><td colspan="6" class="text-center"><i class="fas fa-spinner fa-spin"></i> Loading...</td></tr>'
                        );
                    },
                    success: function(response) {
                        let tbody = '';
                        response.forEach((customer, index) => {
                            tbody += `
                            <tr>
                                <td>${index + 1}</td>
                                <td>${customer.full_name}</td>
                                <td>${customer.phone}</td>
                                <td>${numberFormat(customer.total_orders)}</td>
                                <td>${numberFormat(customer.total_spent)} ৳</td>
                                <td>
                                    <a href="{{ url('customer/details/') }}/${customer.user_id}" 
                                       class="btn btn-sm btn-info">
                                        <i class="fas fa-eye"></i>
                                    </a>
                                </td>
                            </tr>
                        `;
                        });

                        if (response.length === 0) {
                            tbody = `
                            <tr>
                                <td colspan="6" class="text-center">No customers found</td>
                            </tr>
                        `;
                        }

                        document.querySelector('#topCustomersTable tbody').innerHTML = tbody;
