    {{-- Sales Statistics JS Int --}}
    <script type="text/javascript">
        const salesData = @json($salesStats);

        function renderChart(timeRange) {
            let data = salesData[timeRange];

            const options = {
                series: [{
                    name: 'Total Sales',
                    data: data.totalSales,
                    type: 'column'
                }, {
                    name: 'Revenue',
                    data: data.revenue,
                    type: 'column'
                }, {
                    name: 'Total Orders',
                    data: data.numberOfOrders.map(val => val * 1000), // Multiply orders to match scale
                    type: 'column'
                }],
                chart: {
                    type: 'bar',
                    height: 350,
                    toolbar: {
                        show: false
                    }
                },
                plotOptions: {
                    bar: {
                        horizontal: false,
                        columnWidth: '65%', // Increased column width
                        borderRadius: 5,
                        borderRadiusApplication: 'end'
                    },
                },
                colors: ['#16C47F', '#4FABF7', '#FF9D23'],
                dataLabels: {
                    enabled: false
                },
                stroke: {
                    show: true,
                    width: 2,
                    colors: ['transparent']
                },
                xaxis: {
                    categories: data.categories,
                },
                yaxis: {
                    labels: {
                        formatter: function(val) {
                            return (val / 1000).toFixed(1) + 'k';
                        }
                    }
                },
                fill: {
                    opacity: 1
                },
                tooltip: {
                    y: [{
                        formatter: function(val) {
                            return "৳ " + (val / 1000).toFixed(1) + "k";
                        }
                    }, {
                        formatter: function(val) {
                            return "৳ " + (val / 1000).toFixed(1) + "k";
                        }
                    }, {
                        formatter: function(val) {
                            return (val / 1000); // Divide back to get actual order number
                        }
                    }]
                }
            };

            document.querySelector("#salesStatistics").innerHTML = '';
            const chart = new ApexCharts(document.querySelector("#salesStatistics"), options);
            chart.render();
        }

        renderChart('lastMonth');

        document.getElementById('timeRangeSelector').addEventListener('change', function() {
            renderChart(this.value);
        });
    </script>
